ALTER TABLE `version_details` CHANGE `FullVersionNumeric` `FullVersionNumeric` DECIMAL(30,7) UNSIGNED ZEROFILL AS ((case when isnull(`FullVersion`) then NULL else cast((((cast((json_extract(`FullVersionParts`,'$.Major') * pow(10,14)) as unsigned) + cast((json_extract(`FullVersionParts`,'$.Minor') * pow(10,7)) as unsigned)) + cast((json_extract(`FullVersionParts`,'$.Build') * pow(10,0)) as unsigned)) + cast((json_extract(`FullVersionParts`,'$.Revision') / pow(10,7)) as decimal(8,7))) as decimal(30,7)) end)) STORED;